  }
  return (
    <div className="App">
      <h1>One Jam</h1>
      <div className="spotify-container"
      style={{width:"",padding: "10%"}}
      >
        <div className="main-player" style={{wdith: "300px"}}>
          <Player/>
        </div>

      </div>
      <div className="user-container"
      style={{width:"80%", margin: "auto" }}
      >

        <h2
        style={{
          // marginTop: "50px",
          // display:"inline"
        }}
        >
          Users
        </h2>

      <ModalInFunctionalComponent HandleLoginSubmit={HandleLoginSubmit}/>



        {users && users.map(user =>

              <User user_name={user.user_name} user_port={user.user_port} remove={RemoveUser}/>

                    )}

        {/* {users && users.map(user =>

