import {useState } from 'react';

function User(props) {

	const [style, setStyle] = useState();

	function remove(){
		setStyle({display:"none"})

	}

return (	
<div>
	<div className="user-info"
	style={{dispaly:"flex",
	flexDirection:"row",
	}}
	>
		<div style={{display:"inline"}}><strong>{props.user_name} : {props.user_port}</strong></div>
		<button
		style={style}

		onClick={remove}
		>Remove</button>


	</div>
</div>
);

}


export default User;
