class US {
  user_name = "";
  port= "";

}

function App() {

  const [rerender, setRerender] = useState(false);


  let [users, setUsers] = useState(new Array([]));

  function HandleLoginSubmit(name, password ){

    // auth via spotify


    setUsers(oldArray => [...oldArray, {
      user_name:name,
      user_port:"50001"
    }]);

      console.log("user added")

      setRerender(!rerender); 





  }
  return (
    <div className="App">
      <h1>One Jam</h1>
      <div className="spotify-container"
      style={{width:"80%", margin: "auto"}}
      >
        <div className="main-player" style={{wdith: "300px"}}>
          <Player/>
        </div>

      </div>
      <div className="user-container"
      style={{width:"80%", margin: "auto" }}
      >

        <h2
        style={{
          // marginTop: "50px",
          // display:"inline"
        }}
        >
          Users
        </h2>

      <ModalInFunctionalComponent HandleLoginSubmit={HandleLoginSubmit}/>


        {users && users.map(user =>
              <User user_name={user.user_name} user_port={user.user_port}/>

                    )}




      </div>

    </div>
  );
}

export default App;
