  const AddSong = (item) => {
    setPlaylist(oldArray => [...oldArray, item]);
  }
  const RemoveSong = (item) =>{
    setPlaylist((prevState) =>
    prevState.filter((prevItem) => prevItem !== item)
  );
  }

  const RemoveUser = (item) =>{
